'use strict';
var util = require('util');
var id = 0;

function Logger(name) {
  if (!name) {
    name = 'syst';
  }
  if (name.length > 4) {
    name = name.slice(0, 4); 
  }

  // map for 1/2/3
  switch (name.length) {
    case 1: name = ` :${name} `; break;
    case 2: name = ` ${name} `; break;
    case 3: name = `:${name}`; break;
  }
  this.name = name;
}

function createLoggerFunction(level) {
  return function() {
    var now = new Date();
    var line = `<${this.name}> :: ` + util.format.apply(this, arguments);
    console[level](line);
  };
}

Logger.prototype.log = createLoggerFunction('info');
Logger.prototype.info = createLoggerFunction('info');
Logger.prototype.warn = createLoggerFunction('warn');
Logger.prototype.error = createLoggerFunction('error');

module.exports = function(name) {
  var logger = new Logger(name);
  // aliyun log?
  return logger;
};

'use strict';

/**
 * @module multimedia
 */

var native = require('./multimedia.node');
var inherits = require('util').inherits;
var EventEmitter = require('events').EventEmitter;

/**
 * @class MediaPlayer
 */
function MediaPlayer(tag, options) {
  EventEmitter.call(this);
  this._handle = new native.Player(tag);
  this._handle.onevent = this.onevent.bind(this);
}
inherits(MediaPlayer, EventEmitter);

/**
 * @method onevent
 */
MediaPlayer.prototype.onevent = function(type, ext1, ext2, from) {
  var eventName = native.Events[type];
  if (!eventName) {
    this.emit('error', new Error(`cannot find event ${type}`));
    return;
  }
  if (eventName === 'prepared') {
    this._handle.start();
  } else {
    this.emit(eventName, ext1, ext2, from);
  }
};

/**
 * @method play
 * @param {String} url
 */
MediaPlayer.prototype.play = function(url) {
  if (!url)
    throw new Error('url must be a valid string');
  return this._handle.prepare(url);
};

/**
 * @method pause
 */
MediaPlayer.prototype.pause = function() {
  return this._handle.pause();
};

/**
 * @method resume
 */
MediaPlayer.prototype.resume = function() {
  return this._handle.resume();
};

/**
 * @method seek
 * @param {Number} pos
 */
MediaPlayer.prototype.seek = function(pos) {
  return this._handle.seek(pos);
};

/**
 * @method stop
 */
MediaPlayer.prototype.stop = function() {
  return this._handle.stop();
};

/**
 * @method reset
 */
MediaPlayer.prototype.reset = function() {
  return this._handle.reset();
};

/**
 * @method disconnect
 */
MediaPlayer.prototype.disconnect = function() {
  return this._handle.disconnect();
};

/**
 * @property {String} id
 * @readable
 */
Object.defineProperty(MediaPlayer.prototype, 'id', {
  get: function() {
    return this.idGetter();
  }
});

/**
 * @property {Boolean} playing
 * @readable
 */
Object.defineProperty(MediaPlayer.prototype, 'playing', {
  get: function() {
    return this.playingStateGetter();
  }
});

/**
 * @property {Number} duration
 * @readable
 */
Object.defineProperty(MediaPlayer.prototype, 'duration', {
  get: function() {
    return this.durationGetter();
  }
});

/**
 * @property {Number} position
 * @readable
 */
Object.defineProperty(MediaPlayer.prototype, 'position', {
  get: function() {
    return this.positionGetter();
  }
});

/**
 * @property {Boolean} loopMode
 * @readable
 * @writable
 */
Object.defineProperty(MediaPlayer.prototype, 'loopMode', {
  get: function() {
    return this.loopModeGetter();
  },
  set: function(mode) {
    return this.loopModeSetter(mode);
  },
});

/**
 * @property {Number} volume
 * @readable
 * @writable
 */
Object.defineProperty(MediaPlayer.prototype, 'volume', {
  get: function() {
    return this.volumeGetter();
  },
  set: function(vol) {
    return this.volumeSetter(vol);
  }
});

/**
 * @property {String} sessionId
 * @readable
 * @writable
 */
Object.defineProperty(MediaPlayer.prototype, 'sessionId', {
  get: function() {
    return this.sessionIdGetter();
  },
  set: function(id) {
    return this.sessionIdSetter(id);
  }
});

exports.MediaPlayer = MediaPlayer;

/**
 * @module multimedia.soundplayer
 */
exports.soundplayer = require('./soundplayer');

'use strict';

/**
 * @module soundplayer
 */

var child_process = require('child_process');
var url = require('url');
var path = require('path');
var PA_HOME_DIR = process.env.PULSEAUDIO_HOME || '/data/pulseaudio';

function defaultCallback(err) {
  if (err) throw err;
}

/**
 * @method set
 * @param {String} name - the config name
 * @param {any} val
 */
function set(name, val) {
  if (name === 'pulseaudio.homedir') {
    PA_HOME_DIR = val;
  } else {
    throw new Error(`unknown property ${name}`);
  }
}

/**
 * @class SoundPlayer
 * @constructor
 * @param {String} media - the media url to play
 */
function SoundPlayer(media) {
  this._media = media;
  this._subprocess = null;
  this._cmd = '';
}

/**
 * @method play
 * @param {Function} callback <optional> fired when play is done
 * @return {SoundPlayer}
 */
SoundPlayer.prototype.play = function play(callback) {
  var meta = url.parse(this._media);
  if (meta.protocol !== null)
    throw new Error('only support for no protocol url');
  if (typeof callback !== 'function')
    callback = defaultCallback;

  var extname = path.extname(meta.pathname);
  if (extname === '.wav') {
    this._cmd = `paplay ${meta.pathname}`;
  } else if (['.ogg', '.mp3'].indexOf(extname) >= 0) {
    this._cmd = `ffmpeg -i ${meta.pathname} -acodec pcm_s16le -ar 16000 -f wav - | paplay`;
  } else {
    throw new Error('only support for wav/ogg/mp3 formats');
  }
  this._subprocess = child_process.exec(this._cmd, {
    env: { 'HOME': PA_HOME_DIR },
  }, function ondone(err, stdout, stderr) {
    if (err)
      return callback(new Error(stderr));
    callback(null, stdout);
  });
  return this;
};

/**
 * @method stop
 * @return {SoundPlayer}
 */
SoundPlayer.prototype.stop = function stop() {
  if (this._subprocess) {
    this._subprocess.kill(0);
    this._subprocess = null;
  }
  return this;
};

/**
 * @method play
 * @param {String} media - the media url to play
 * @return {SoundPlayer}
 */
function play(media, callback) {
  var splayer = new SoundPlayer(media);
  return splayer.play(callback);
}

exports.set = set;
exports.play = play;

